A=matrix(QQ, [[5,3,1], [1,4,-2]]);
A.swap_rows(0,1); #swaps row 0 and row 1
A

A=matrix(QQ, [[5,3,1], [1,4,-2]]);
A.swap_rows(0,1);
A.add_multiple_of_row(1,0,-5); #adds -5*row 0 to row 1
A

A=matrix(QQ, [[5,3,1], [1,4,-2]]);
A.swap_rows(0,1);
A.add_multiple_of_row(1,0,-5);
A.rescale_row(1, -1/17); #multiplies row 1 by -1/17
A
